"use strict";

/*!
 * Module dependencies.
 */

var dummy
  , hotplate = require('hotplate')
  , path = require('path')
;


hotplate.hotEvents.on( 'pageElements', 'hotCoreHandyCss', function( done ){

  done( null, {
    csses: [ 'handyCss.css' ]
  });

});


hotplate.hotEvents.on( 'clientPath', 'hotCoreHandyCss', function( done ){
  done( null, path.join(__dirname, '../client') );
});


