"use strict";

var dummy
  , hotplate =  require('hotplate')
  , path = require('path')
;


hotplate.hotEvents.on( 'dojoModulesPerPage', 'hotDojoStores', function( req, pageName, done ){
  done( null, [ 'AppStoreNotify' ] );
});


// Simply activate path to client files
hotplate.hotEvents.on( 'clientPath', 'hotDojoStores', function( done ){
  done( null, path.join(__dirname, '../client') );
})


