define([

  "dojo/_base/declare"
, "dojo/_base/lang"

, "dojo/topic"

], function(

  declare
, lang

, topic

){

  return  declare(null, {

    templatedHooks: true,

    constructor: function(){
      this.templatedHooks = true;
      topic.publish('hotplate/hotDojoWidgetHooks/constructor/' + this.id, this);
    },

    buildRendering: function(){
      topic.publish('hotplate/hotDojoWidgetHooks/buildRendering/before/' + this.id, this);
      this.inherited(arguments);
      topic.publish('hotplate/hotDojoWidgetHooks/buildRendering/after/' + this.id, this);
    },

    destroyRendering: function(){
      topic.publish('hotplate/hotDojoWidgetHooks/destroyRendering/before/' + this.id, this);
      this.inherited(arguments);
      topic.publish('hotplate/hotDojoWidgetHooks/destroyRendering/after/' + this.id, this);
    },

    postCreate: function(){
      topic.publish('hotplate/hotDojoWidgetHooks/postCreate/before/' + this.id, this);
      this.inherited(arguments);
      topic.publish('hotplate/hotDojoWidgetHooks/postCreate/after/' + this.id, this);
    },

    startup: function(){
      topic.publish('hotplate/hotDojoWidgetHooks/startup/before/' + this.id, this);
      this.inherited(arguments);
      topic.publish('hotplate/hotDojoWidgetHooks/startup/after/' + this.id, this);

    },

    destroy: function(){
      topic.publish('hotplate/hotDojoWidgetHooks/destroy/before/' + this.id, this);
      this.inherited(arguments);
      topic.publish('hotplate/hotDojoWidgetHooks/destroy/after/' + this.id, this);
    }

  });
});

