"use strict";

var dummy
  , hotplate =  require('hotplate')
  , path = require('path')

  , hotDojoStores = require('hotDojoStores') // CLIENT
  , hotDojoGlobals = require('hotDojoGlobals') // CLIENT
  , hotDojoWidgets = require('hotDojoWidgets') // CLIENT

;

// The Dojo side of things subscribes to newStore. Pre-loading it, making sure it runs before the app does
hotplate.hotEvents.on( 'dojoModulesPerPage', 'hotDojoComet', function( req, pageName, done ){
  done( null, [ 'messages' ] );
});


// Simply activate path to client files
hotplate.hotEvents.on( 'clientPath', 'hotDojoComet', function( done ){
  done( null, path.join(__dirname, '../client') );
})


